import axios from "axios"
import emitter from "tiny-emitter/instance"
import cometd from "cometd"
import cometdAdapter from "cometd-nodejs-client"

import Helpers from "./helpers"
import Player from "./player"

if (typeof window === "undefined") {
  cometdAdapter.adapt()
}

/**
 * Client class, used as controller for players.
 * Also initializes the socket communication.
 *
 * @class Client
 */
export default class Client extends Player {
  /**
   * @param {Number} pin The game pin
   * @param {String} name Name of the main player
   * @param {String} proxy Optional cors proxy url for the browser
   */
  constructor(pin, name, proxy = "") {
    super(null, Helpers.randomCid(), pin)

    this.pin = pin
    this.name = name
    this.proxy = proxy
    this.emitter = emitter
    this.cometd = null
    this.twoFactor = null
  }

  /**
   * Checks session and initializes communication, must be called first!
   * 
   * @return {Promise<Object>} Returns CometD client
   */
  initialize() {
    return this.checkSession()
      .then(this.createSocket)
      .then(e => this.cometd = e)
  }

  /**
   * Initializes CometD client
   * 
   * @return {Promise<Object>} Returns CometD client
   */
  createSocket(parsed) {
    const socket = new cometd.CometD()
    socket.configure({
      url: `https://kahoot.it/cometd/${parsed.pin}/${parsed.session}`
    })
    socket.websocketEnabled = true

    return new Promise((resolve, reject) => {
      socket.handshake((h) => {
        if (!h.successful) return reject(Error("session failed decoding"))
        socket.subscribe("/service/controller", m => emitter.emit("controller", m))
        socket.subscribe("/service/player", m => emitter.emit("player", m))
        socket.subscribe("/service/status", m => emitter.emit("status", m))

        resolve(socket)
      })
    })
  }

  /**
   * Checks existance of pin
   * 
   * @return {Promise<Object>} Returns object containing session information
   */
  checkSession() {
    return axios.get(`${this.proxy}https://www.kahoot.it/reserve/session/${this.pin}/?${Helpers.getTime()}`)
      .then((res) => {
        const sessionInfo = res.data
        const headers = res.headers
        const rawSession = headers["x-kahoot-session-token"]
        const solvedChallenge = Helpers.solveChallenge(sessionInfo.challenge)
        const twoFactor = sessionInfo.twoFactorAuth
        const session = Helpers.shiftBits(rawSession, solvedChallenge)

        this.twoFactor = twoFactor

        return {
          pin: this.pin,
          twoFactor: twoFactor,
          session: session
        }
      })
  }

  /**
   * Adds main player to the game, this is normally called after initialize to get other functions working.
   * Automatically bruteforces 2FA
   * 
   * @return {Promise<>} Returns when complete
   */
  doLogin() {
    this.sendPacket(
        "/service/controller", {
          gameid: this.pin,
          host: "kahoot.it",
          name: this.name,
          type: "login"
        })
      .then(() => {
        return new Promise((resolve, _) => {
          if (this.twoFactor) {
            setTimeout(() => {
              this.bruteForceTwoFactor()
              resolve()
            }, 350)
          }
        })
      })
  }

  /**
   * Adds a player to the game
   * 
   * @param {String} name The name of the player
   * @param {String} cid The unique id of the player, helper function randomCid can be used for this
   * @param {Boolean} isGhost Makes player ghost or normal
   * 
   * @return {Player} Returns object containing session information
   */
  addPlayer(name, cid, isGhost) {
    this.sendPacket(`/controller/${this.pin}`, {
      type: "joined",
      cid: cid,
      image: "@idiidk",
      name: name,
      isGhost: isGhost,
      completedTwoFactorAuth: 1
    })

    return new Player(this.cometd, cid, this.pin);
  }
}

import atob from "atob"

/**
 * Helper class, contains static functions used when authenticating
 *
 * @class Helpers
 */
export default class Helpers {
  /**
   * Gets unix time
   * 
   * @return {String} Time in macroseconds
   */
  static getTime() {
    return new Date().getTime()
  }

  /**
   * Does some magic by xor'ing the session and challenge to generate a url
   * 
   * @param {String} session Session string
   * @param {String} challenge Challenge string
   * 
   * @return {String} Parsed session
   */
  static shiftBits(session, challenge) {
    const sessionBytes = this.convertDataToBinary(atob(session))
    const challengeBytes = this.convertDataToBinary(challenge)
    const bytesList = []
    for (let i = 0; i < sessionBytes.length; i++) {
      bytesList.push(
        String.fromCharCode(
          sessionBytes[i] ^ challengeBytes[i % challengeBytes.length]
        )
      )
    }
    return bytesList.join("");
  }

  /**
   * Converts any data to binary data
   * 
   * @param raw Data to convert
   * 
   * @return {String} Parsed session
   */
  static convertDataToBinary(raw) {
    const rawLength = raw.length;
    const tempArray = new Uint8Array(new ArrayBuffer(rawLength));

    for (let i = 0; i < rawLength; i++) {
      tempArray[i] = raw.charCodeAt(i);
    }
    return tempArray;
  }

  /**
   * Solves challenge to get the first part of authentication out of the way
   * 
   * @param {String} challenge Challenge code
   * 
   * @return {String} Parsed challenge
   */
  static solveChallenge(challenge) {
    let toDecode = challenge.split("'")[1].split("'")[0];
    const offset = eval(challenge.split("var offset = ")[1].split(";")[0]);
    const decodeMod = parseInt(
      challenge
      .split(") % ")[1]
      .split(")")[0]
      .trim()
    );
    const decodePlus = parseInt(
      challenge
      .split(decodeMod)[1]
      .split("+ ")[1]
      .split(")")[0]
    );
    let final = "";

    for (let i = 0; i < toDecode.length; i++) {
      const char = toDecode[i];
      final += String.fromCharCode(
        ((char.charCodeAt(0) * i + offset) % decodeMod) + decodePlus
      );
    }

    return final;
  }

  /**
   * Generates random id for players
   * 
   * @return {String} Random cid
   */
  static randomCid() {
    return 10000 + Math.floor(Math.random() * 10000)
  }
}

import Client from "./client"
import Helpers from "./helpers"

class Kahoot {
  constructor() {
    this.Client = Client
    this.Helpers = Helpers
  }
}

export default new Kahoot()

import axios from "axios"

/**
 * Information class, used for communication with the Kahoot quiz gateway
 *
 * @class Information
 */
export default class Information {

  constructor() {
    this.client = axios.create({
      baseURL: "https://create.kahoot.com"
    });
  }

  /**
   * Authenticates to kahoot, must be called first
   * 
   * @param {String} username Username to login to create.kahoot.com
   * @param {String} password Password to login to create.kahoot.com
   * @return {Promise<Error>} Error
   */
  authenticate(username, password, callback) {
    headers = {
      "x-kahoot-login-gate": "enabled",
      "Content-Type": "application/json"
    }
    data = {
      user_name: username,
      password: password,
      grant_type: "password"
    }
    this.client.post("/rest/authenticate", data, {
        headers: headers
      })
      .catch((error) => {
        callback(error);
      })
      .then((resp) => {
        const response = resp.response.data;
        this.bearer_token = response.access_token;
        this.token_expiration = response.expires;
        this.client.headers["Authorization"] = "Bearer " + response.access_token;
        callback(null);
      })
  }
  /**
   * Searches kahoots for the specified term
   * @param {String} query search term
   * @param {Promise<Object>} callback axios response
   */
  search(query, callback) {
    params = {
      query: query.replace(/[.,\/#!$%\^&\*;:{}=\-_`~()]/g, ""),
      _: new Date().getTime(),
      limit: 100
    }
    callback(this.client.get("/rest/kahoots/search/public", {
      params: params
    }))
  }
  /**
   * Searches kahoots for the specified term
   * @param {String} uuid game id
   * @param {Promise<Object>} callback axios response
   */
  getGame(callback) {
    params = {
      _: new Date().getTime()
    }
    callback(this.client.get("/rest/kahoots/uuid", {
      params: params
    }))
  }
}

import EVENTS from "./events"

/**
 * Player class, created when calling addPlayer. 
 * Contains player related functions
 *
 * @class Player
 */
export default class Player {
  /**
   * @param {Object} cometd CometD instance
   * @param {String} cid Cid of the player
   * @param {String} pin Pin of the current game
   */
  constructor(cometd, cid, pin) {
    this.cometd = cometd
    this.cid = cid
    this.pin = pin
  }

  /**
   * Sends a packet to Kahoot
   * 
   * @param {String} channel Channel to send to
   * @param {Object} data Data to send to channel specified
   * 
   * @return {Promise} Resolves when done
   */
  sendPacket(channel, data = {}) {
    return new Promise((resolve, _) => {
      data.host = "play.kahoot.it"
      data.gameid = this.pin
      this.cometd.publish(channel, data, resolve)
    })
  }

  /**
   * Attempts 2FA login using code, 0000 to 3333
   * 
   * @param {String} code 0000 to 3333
   */
  twoFactorLogin(code) {
    this.sendPacket(
      "/service/controller", {
        id: EVENTS.submitTwoFactorAuth,
        type: "message",
        cid: this.cid,
        gameid: this.pin,
        host: "kahoot.it",
        content: JSON.stringify({
          sequence: code
        })
      }
    )
  }

  /**
   * Attempts all 2FA codes until logged in
   */
  bruteForceTwoFactor() {
    let combinations = [
      "0123",
      "0132",
      "0213",
      "0231",
      "0321",
      "0312",
      "1023",
      "1032",
      "1203",
      "1230",
      "1302",
      "1320",
      "2013",
      "2031",
      "2103",
      "2130",
      "2301",
      "2310",
      "3012",
      "3021",
      "3102",
      "3120",
      "3201",
      "3210"
    ];
    combinations.forEach(c => {
      this.twoFactorLogin(c);
    })
  }

  /**
   * Remove this player from the game
   */
  removeFromGame() {
    this.send(`/controller/${this.pin}`, {
      type: "left",
      cid: this.cid,
      client: "@idiidk"
    })
  }

  /**
   * Send team name array, only works when the game is a team game
   * 
   * @param {Array} names The names of teammates you want to add
   */
  sendTeam(names) {
    this.send(`/controller/${this.pin}`, {
      id: EVENTS.joinTeamMembers,
      type: "message",
      cid: this.cid,
      content: JSON.stringify(names)
    })
  }

  /**
   * Sends a game answer for the current question
   * 
   * @param {Number} answerId 0 to 3
   */
  sendGameAnswer(answerId) {
    this.send(`/controller/${this.pin}`, {
      id: EVENTS.gameBlockAnswer,
      type: "message",
      cid: this.cid,
      content: JSON.stringify({
        choice: answerId,
        meta: {
          lag: 15,
          device: {
            userAgent: "Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Ubuntu Chromium/61.0.3163.79 Chrome/61.0.3163.79 Safari/537.36",
            screen: {
              width: 1920,
              height: 1080
            }
          }
        }
      })
    })
  }

  /**
   * Disconnect from the game
   */
  disconnect() {
    this.cometd.disconnect();
  }
}
